mapper.delete:
DELETE FROM tbl_job_open_stack
WHERE j_no = #{jNo}
AND stack_cd IN

    #{skill}

DELETE
FROM tbl_file
WHERE file_id = #{fileId}
  AND file_ref_id = #{refId}

mapper.update:
update tbl_resume_job_open
set jr_gbn_cd = #{type}
where jr_no = #{jrNo}

mapper.select:
SELECT COUNT(*) > 0
FROM tbl_resume_job_open
WHERE j_no = #{jNo}
  AND r_no = #{rNo}
  AND inst_id = #{id};

mapper.update:
update tbl_job_open
set del_yn = 'Y'
where j_no = #{jNo}
