mapper.select:
SELECT a.*, COALESCE((SELECT GROUP_CONCAT(DISTINCT b1.stack_cd ORDER BY b1.stack_cd SEPARATOR ',') FROM tbl_job_open_stack b1 WHERE b1.j_no = a.j_no), '') AS skills, COALESCE(f.file_url, '') AS fileUrl FROM tbl_job_open a LEFT JOIN tbl_file f ON a.id = f.file_ref_id AND f.file_gbn_cd = 100 WHERE (a.j_title LIKE CONCAT('%', #{searchText}, '%') OR a.j_content LIKE CONCAT('%', #{searchText}, '%'))
AND EXISTS ( SELECT 1 FROM tbl_job_open_stack b2 WHERE b2.j_no = a.j_no AND b2.stack_cd IN #{skill} )
GROUP BY a.j_no LIMIT #{offset}, #{limit};

SELECT COUNT(DISTINCT a.j_no) FROM tbl_job_open a LEFT JOIN tbl_job_open_stack b ON a.j_no = b.j_no WHERE (a.j_title LIKE CONCAT('%', #{searchText}, '%') OR a.j_content LIKE CONCAT('%', #{searchText}, '%'))
AND b.stack_cd IN #{skill}
;
